    public int findFloor(string data)
    {
        int floor = 0;

        foreach (var ch in data)
        {
            if (ch == '(')
                floor++;
            if (ch == ')')
                floor--;
        }

        return floor;
    }

    public int findBasement(string data)
    {
        int floor = 0;
        int index = 0;

        foreach (var ch in data)
        {
            index++;
            if (ch == '(')
                floor++;
            if (ch == ')')
                floor--;
            if (floor == -1)
                return index;
        }

        return index;
    }
